#pragma once// ===========================================================================//	CQCamPane.h						A pane using the newly created CQuickCam obj// ===========================================================================//	See © notices in CQuickCam.*//	12/02/95	Finsihed CQuickCam enought to try this out.//	12/20/95	This is a mindlessly simple implementation and test bead for the //			CQuickCam class.  This class contains the features needed to control//			the vdig component.  Inititally these two classes, CQuickCam and LPane//			were combined to form one class.  This separation allowed the development//			of video input panes dependant on other vdig components.#include	<LPane.h>#include	<LPeriodical.h>#include	<QuickTimeComponents.h>#include	"CVDig.h"class	CGrabPane : public LPane, public LPeriodical, public CVDig {public:	enum { class_ID = 'grab' };	static CGrabPane*	CreateGrabPaneStream(LStream *inStream);					CGrabPane( void );					CGrabPane(LStream *inStream);	virtual			~CGrabPane( void );		virtual void	DrawSelf();				virtual	void	SpendTime(const EventRecord &inMacEvent);	virtual	void	ClickSelf(const SMouseDownEvent &inMouseDown);protected:	virtual void	FinishCreateSelf();private:	PicHandle				grabPict;		// Handle to captured picture	Rect					oldGlobalFrame;};